<template>
  <div>
    <v-row no-gutters v-for="row in rowArr" :key="row.index">
      <v-col class="idxClumn" cols="2" sm="2">
        <v-card class="pa-2" outlined tile> {{ row.index }} </v-card>
      </v-col>
      <v-col class="textClumn" cols="10" sm="10">
        <v-card class="pa-2" outlined tile>
          <span v-html="row.text"> </span>
        </v-card>
      </v-col>
    </v-row>
  </div>
</template>

<script>
export default {
  name: "Editor",
  data: () => ({
    rowArr: [],
    font_size: 16,
    stream_index: 0,
    json_data: {
      text:
        "",
      annotations: [
        { index: 1, length: 20, tooltip: "Annotation 1" },
        { index: 33, length: 20, tooltip: "Annotation 2" },
        { index: 130, length: 20, tooltip: "Annotation 3" },
        { index: 700, length: 20, tooltip: "Annotation 4" },
        { index: 730, length: 20, tooltip: "Annotation 5" },
        { index: 4000, length: 20, tooltip: "Annotation 6" },
        { index: 28000, length: 20, tooltip: "Annotation 7" },
        { index: 28100, length: 20, tooltip: "Annotation 8" },
        { index: 34000, length: 20, tooltip: "Annotation 9" },
        { index: 34010, length: 20, tooltip: "Annotation 10" },
      ],
    },
  }),
  methods: {
    getText(row) {
      let rowsize = this.charsPerRow()
      let hOpenTag = '<span class="highlightText">'
      let closeTag = '</span>'
      let phraseStartIdx = Number(row * rowsize)
      let phraseEndIdx = (1 + Number(row) ) * rowsize
      let txtPhrase = this.json_data.text.substring(phraseStartIdx,phraseEndIdx);
      this.json_data.annotations.map((v) => {
        let tmpStr = '';
        let markerfirstIdx = v.index + phraseStartIdx;
        let markerScndIdx = markerfirstIdx + v.length;
        let firstIdxfound = markerfirstIdx >= phraseStartIdx && markerfirstIdx <= phraseEndIdx;
        let scndIdxFound = markerScndIdx >= phraseStartIdx && markerScndIdx <= phraseEndIdx;

        if (firstIdxfound && scndIdxFound ) {
          tmpStr += this.json_data.text.substring(phraseStartIdx, markerfirstIdx);
          tmpStr += hOpenTag + this.json_data.text.substring(markerfirstIdx, markerScndIdx) + closeTag;
          tmpStr += this.json_data.text.substring(markerScndIdx, phraseEndIdx);
          txtPhrase = tmpStr;
          phraseStartIdx += hOpenTag.length + closeTag.length
          phraseEndIdx += hOpenTag.length + closeTag.length
        } else if (firstIdxfound) {
            tmpStr += this.json_data.text.substring(phraseStartIdx, markerfirstIdx);
            tmpStr += hOpenTag + this.json_data.text.substring(markerfirstIdx, phraseEndIdx) + closeTag;
            txtPhrase = tmpStr;
            phraseStartIdx += hOpenTag.length + closeTag.length
            phraseEndIdx += hOpenTag.length + closeTag.length
        } else if (scndIdxFound) {
            tmpStr += hOpenTag + this.json_data.text.substring( phraseStartIdx, markerScndIdx ) + closeTag;
            txtPhrase = tmpStr;
            phraseStartIdx += hOpenTag.length + closeTag.length
            phraseEndIdx += hOpenTag.length + closeTag.length
        }        
      });
      return txtPhrase;
    },
    charsPerRow() {
      return Math.floor(((window.innerWidth / 12) * 10) / this.font_size);
    },
    rows() {
      let len = Math.floor(this.json_data.text.length / this.charsPerRow());
      let arr = [...Array(len).keys()];
      return arr;
    },
  },
  beforeMount: function () {
    for (let row in this.rows()) {
       this.rowArr.push({
         index: row * this.charsPerRow(),
         text: this.getText(row)
       })
      }
  }
}
</script>

<style scoped>
/deep/ .highlightText {
  background-color:rgba(0, 0, 255, 0.3);
}
</style>